	//Returns my solution's directory
	#define TEST_CASE_DIRECTORY GetDirectoryName(__FILE__)

	std::string GetDirectoryName(std::string path)
	{
		const size_t last_slash_idx = path.rfind('\\');
		if (std::string::npos != last_slash_idx)
		{
			return path.substr(0, last_slash_idx + 1);
		}
		return "";
	}

	void CompareOutput(std::string& outputFile, std::string& expectedCostOfAlignment, std::string& expectedFirstStringAlignment, std::string& expectedSecondStringAlignment)
	{
		std::ifstream file(outputFile);
		Assert::IsTrue(file.is_open());

		std::string costOfAlignment = "";
		std::string firstStringAlignment = "";
		std::string secondStringAlignment = "";
		std::vector<std::string> lines;
		std::string line;
		int atLine = 0;
		while (std::getline(file, line))
		{
			if (atLine == 0) //CostOfAlignment
			{
				costOfAlignment = line;
			}
			else if (atLine == 1) //FirstStringAlignment
			{
				firstStringAlignment = line;
			}
			else if (atLine == 2) //SecondStringAlignment
			{
				secondStringAlignment = line;
			}
			else
			{
				break;
			}

			atLine++;
		}

		file.close();
		Assert::AreEqual(expectedCostOfAlignment + '\n' + expectedFirstStringAlignment + '\n' + expectedSecondStringAlignment, costOfAlignment + '\n' + firstStringAlignment + '\n' + secondStringAlignment);
	}

